import React, { useState, useEffect } from "react";
import EditIcon from "./Icons/EditIcon";
import DeletIcon from "./Icons/DeletIcon";
import AddEditMobileForm from "./AddEditMobileForm";
import { Modal } from "@nextui-org/react";
import axios from "axios";

function ListOfMobileSubs() {
  const [openEditForm, setopenEditForm] = useState(false);
  const [editMode, seteditMode] = useState(false);
  const [getSubs, setgetSubs] = useState([]);

  const showEditForm = () => {
    setopenEditForm(true);
    seteditMode(true);
  };

  const closeEditForm = () => {
    setopenEditForm(false);
  };

  // delete subscriber
  const deleteSub = async () => {
    axios
      .delete("/endpoint")
      .then((response) => {
        console.log("#############", response);
      })
      .catch((error) => {
        console.log("#############", error);
      });
  };

  // fetch lists of mobile subscribers
  const fetchSubs = async () => {
    axios
      .get("http://localhost:3001/mobile-sub")
      .then((response) => {
        setgetSubs(response.data);
      })
      .catch((error) => {
        console.log("#############", error);
      });
  };

  useEffect(() => {
    fetchSubs();
  }, []);

  return (
    <div>
      {getSubs.map((subs, index) => {
        return (
          <div key={index} className="flex justify-between border-2 rounded-lg w-full p-5 hover:border-blue-700 hover:shadow-lg transition ease-in-out delay-150 hover:-translate-y-1 hover:scale-20">
            <div className="flex justify-start">
              <div className="pl-2 pr-2">{subs.customer_id_user}</div>
              <div className="md:pl-5 pl-2 pr-2">{subs.msisdn}</div>
              <div className="md:pl-5 pl-2 pr-2">{subs.service_type}</div>
              <div className="md:pl-5 pl-2 pr-2">{subs.service_start_date}</div>
            </div>

            <div className="md:flex md:justify-between hidden">
              <div className="pl-2 pr-2" onClick={showEditForm}>
                <EditIcon />
              </div>
              <div className="pl-2 pr-2" onClick={deleteSub}>
                <DeletIcon />
              </div>
            </div>
          </div>
        );
      })}

      <Modal
        preventClose
        width="600px"
        closeButton
        aria-labelledby="modal-title"
        open={openEditForm}
        onClose={closeEditForm}
      >
        <AddEditMobileForm editMode={editMode} />
      </Modal>
    </div>
  );
}

export default ListOfMobileSubs;
