import React, { useState } from "react";
import axios from "axios";

const mobileServiceType = [{ name: "Prepaid" }, { name: "Postpaid" }];

function MobileSubscribers() {
  const [searchValue, setSearchValue] = useState("");
  const [filterValue, setfilterValue] = useState("");

  // logic to search through phone numbers
  const filterPhone = () => {
    const data = {
      msisdn: searchValue,
      service_type: filterValue,
    };

    console.log("###############", data);
    axios
      .post("/endpoint", data)
      .then((response) => {
        console.log("###############", response);
      })
      .catch((error) => {
        console.log("###############", error);
      });
  };

  return (
    <div className="my-10">
      <div className=" h-full">

      <div class="grid md:grid-cols-3 grid-cols-1 gap-4">
        <div className="">
        <input
              value={searchValue}
              placeholder="Search mobile"
              onChange={(e) => setSearchValue(e.target.value)}
              className="h-10 rounded-lg border-2 px-2 focus:outline-gray-400 w-full"
            />
        </div>
        <div className="">
        <input
              value={searchValue}
              placeholder="Search mobile"
              onChange={(e) => setSearchValue(e.target.value)}
              className="h-10 rounded-lg border-2 px-2 focus:outline-gray-400"
            />
        </div>
        <div className="flex justify-between border-2 border-gray-400 rounded-md items-center h-20 p-5 hover:border-blue-700 hover:shadow-lg transition ease-in-out delay-150 hover:-translate-y-1 hover:scale-20">
            <div className="font-bold text-lg">POSTPAID NUMBERS</div>
            <div  className="font-bold text-lg">25</div>
        </div>
      </div>
      
        <div className="flex md:justify-end justify-center mt-10">
          <button className="bg-blue-700 md:w-40 w-full rounded-lg h-10 text-white">
            Add Subscriber
          </button>
        </div>
      </div>
    </div>
  );
}

export default MobileSubscribers;
