import React, { useState } from "react";
import {
  Modal,
  Button,
  Text,
  Input,
  Row,
  Grid,
  Loading,
  Link,
} from "@nextui-org/react";
import axios from "axios";

function AddEditMobileForm() {
  const [Id, setId] = useState("");
  const [msisdn, setmsisdn] = useState("");
  const [customer_id_owner, setcustomer_id_owner] = useState("");
  const [customer_id_user, setcustomer_id_user] = useState("");
  const [service_type, setservice_type] = useState("");
  const [service_start_date, setservice_start_date] = useState("");


  // logic to add/edit mobile subscribers
//   const addEditForm = () => {
//       if(){

//       }else{

//       }
//   }

    // logic to addMobileSubs 
    const addMobileSubs = () => {
        const data = {
            Id : Id,
            msisdn : msisdn,
            customer_id_owner : customer_id_owner,
            customer_id_user : customer_id_user,
            service_type : service_type,
            service_start_date : service_start_date
        }

        axios.post("")
    }

    // logic to editMobileSubs 
    const editMobileSubs = () => {
        const data = {
            Id,
            msisdn,
            customer_id_owner,
            customer_id_user,
            service_type,
            service_start_date
        }
    }

  return (
    <div>
      <Modal.Header>
        <Text id="modal-title" size={18}>
          <Text b size={18}>
            Add Phone Number
          </Text>
        </Text>
      </Modal.Header>
      <Modal.Body>
        <div className="grid md:grid-cols-2 grid-cols-1 ">
          <div className="mx-1 mb-2">
            <input
              value={Id}
              placeholder="Id"
              onChange={(e) => setId(e.target.value)}
              className="w-full h-10 rounded-lg border-2 px-2 focus:outline-gray-400"
            />
          </div>
          <div className="mx-1">
            <input
              value={msisdn}
              placeholder="Phone Number"
              onChange={(e) => setmsisdn(e.target.value)}
              className="w-full h-10 rounded-lg border-2 px-2 focus:outline-gray-400"
            />
          </div>
        </div>
        <div className="grid md:grid-cols-2 grid-cols-1 ">
          <div className="mx-1 mb-2">
            <input
              value={customer_id_owner}
              placeholder="Owner Id"
              onChange={(e) => setcustomer_id_owner(e.target.value)}
              className="w-full h-10 rounded-lg border-2 px-2 focus:outline-gray-400"
            />
          </div>
          <div className="mx-1">
            <input
              value={customer_id_user}
              placeholder="User Id"
              onChange={(e) => setcustomer_id_user(e.target.value)}
              className="w-full h-10 rounded-lg border-2 px-2 focus:outline-gray-400"
            />
          </div>
        </div>
        <div className="grid md:grid-cols-2 grid-cols-1 ">
          <div className="mx-1 mb-2">
            <input
              value={service_type}
              placeholder="Service Type"
              onChange={(e) => setservice_type(e.target.value)}
              className="w-full h-10 rounded-lg border-2 px-2 focus:outline-gray-400"
            />
          </div>
          <div className="mx-1">
            <input
              value={service_start_date}
              placeholder="Start Date"
              onChange={(e) => setservice_start_date(e.target.value)}
              className="w-full h-10 rounded-lg border-2 px-2 focus:outline-gray-400"
            />
          </div>
        </div>
      </Modal.Body>
      <Modal.Footer>
        <Button auto size="lg" rounded></Button>
      </Modal.Footer>
    </div>
  );
}

export default AddEditMobileForm;
