import React, { useState } from "react";
import axios from "axios";

const mobileServiceType = [{ name: "Prepaid" }, { name: "Postpaid" }];

function MobileSubscribers() {
  const [searchValue, setSearchValue] = useState("");
  const [filterValue, setfilterValue] = useState("");

  // logic to search through phone numbers
  const filterPhone = () => {
    const data = {
      msisdn: searchValue,
      service_type: filterValue,
    };

    console.log("###############", data);
    axios
      .post("/endpoint", data)
      .then((response) => {
        console.log("###############", response);
      })
      .catch((error) => {
        console.log("###############", error);
      });
  };

  return (
    <div className="my-10">
      <div className=" h-full">
        <div className="grid md:grid-cols-3 grid-cols-1 gap-4">
          <div>
            <input
              value={searchValue}
              placeholder="Search mobile"
              onChange={(e) => setSearchValue(e.target.value)}
              className="w-full h-10 rounded-lg border-2 px-2 focus:outline-gray-400 placeholder:co"
            />
          </div>
          <div>
            <select
              className="w-full h-10 rounded-lg focus:outline-gray-400 border-2"
              onChange={(e) => {
                setfilterValue(e.target.value);
              }}
            >
              {mobileServiceType.map((serviceType, index) => {
                return (
                  <option value={serviceType.name} key={index}>
                    {serviceType.name}
                  </option>
                );
              })}
            </select>
          </div>
          <div>
            <button
              className="w-full bg-blue-700 rounded-lg h-10 text-white"
              onClick={filterPhone}
            >
              Search
            </button>
          </div>
        </div>

        <div className="flex md:justify-end justify-center mt-10">
          <button className="bg-blue-700 md:w-40 w-full rounded-lg h-10 text-white">
            Add Subscriber
          </button>
        </div>
      </div>
    </div>
  );
}

export default MobileSubscribers;
