import React, {useState} from "react";
import axios from "axios";

const mobileServiceType = [
    {
        "name" : ""
    }
]

function MobileSubscribers() {
    const [searchValue, setSearchValue] = useState("")
    const [filterValue, setfilterValue] = useState("")

    // logic to search through phone numbers
    const filterPhone = () => {
        const data = {
            msisdn: searchValue,
            service_type: filterValue
        }

        console.log("###############", data)
        axios.post("/endpoint", data )
        .then((response) => {
            console.log("###############", response)
        }).catch((error) => {
            console.log("###############", error)
        })
    }

  return (
    <div className="my-10">
      <div className=" h-full">
        <div className="grid md:grid-cols-3 grid-cols-1 md:gap-4 gap-y-4 gap-x-0 ">
          <div className="col-span-2">
              <div className="flex ">
                    <div>
                        <input value={searchValue} placeholder="Search mobile" onChange={(e) => setSearchValue(e.target.value)} className="h-10 md:w-96 w-60 rounded-lg border-2 px-2 focus:outline-gray-400" />
                    </div>
                    <div>
                        <button className="bg-blue-700 md:w-40 w-20 rounded-lg h-10 ml-5 text-white" onClick={filterPhone}>Search</button>
                    </div>
              </div>
          </div>
          <div>
              <select  className="w-full h-10 rounded-lg focus:outline-gray-400 border-2" onClick={filterPhone}>
                  <option value="Prepaid">Prepaid</option>
                  <option  value="postpaid">Postpaid</option>
              </select>
          </div>
        </div>
        <div className="flex md:justify-end justify-center mt-10">
        <button className="bg-blue-700 md:w-40 w-full rounded-lg h-10 text-white">Add Subscriber</button>
        </div>
      </div>
    </div>
  );
}

export default MobileSubscribers;
